package two_datatypes;

public class EscapeSequences {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("There are certain use cases, where we have to print some characters but we cannot use them directly");
		System.out.println("suppose I want to print something in quotes");
		System.out.println("In that case, escape sequence comes into picture");
		System.out.println("Escape sequence consists of two characters");
		System.out.println("Fist is backslash, second is character that we want to escape.");
		System.out.println("Hello \"Java\" Coder");
		System.out.println("See above how java is printed with escape sequence");
		System.out.println("There are certain escape sequences like \\n for new line, \\t for a new tab");
		
		//some common escape sequences are \", \\, \n, \t
	}

}
