package two_datatypes;

public class IntegerNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Integer are again: 1.byte(-128 to 127 1 Byte) 2.short(-32768 to 32767 2 Byte) 3.int(-2147483648 to 2147483647 4 byte) 4.long( 8 byte)");
		System.out.println("If data type is long but range of literal is within the int, no need to use l suffix");
		System.out.println("But if literal range is outside int and is being stored inside long data type, you have to suffix the number with L or l");
	}

}
