package two_datatypes;

public class KeywordsAndIdentifiers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Keywords are already available in java and cannot be used manually");
		System.out.println("keywords are always in lowercase like null, true, false");
		
		//identifiers
		System.out.println("These are names given to classes, methods and vars");
		System.out.println("Cannot start with number");
		System.out.println("keywords cannot be used");
		System.out.println("can only use letters, numbers, _ and $");
		System.out.println("since java 9 _ is not allowed");
		System.out.println("camelcase is prefered for variables and methods");
		System.out.println("for classes, the conventions are different. Rather than camelcase, capital case is used");
		System.out.println("abc$true is a valid identifier");
	}

}
